import React from "react";
import Header2 from "./header2";
import Product_list2 from "./product_list2";
import CartList from "./cartList";


function Shopping_cart(){
    const [product, setProduct] = React.useState([
      {
        id: "01",
        name: "abc_plug",
        category: "plug",
        brand: "xyz_brand",
        price: "800.00",
        url: "./img/plug.png"
    },
    {
        id: "02",
        name: "abc_bulb",
        category: "bulb",
        brand: "efg_brand",
        price: "900.00",
        url: "./img/bulb.jpg"
    },
    {
        id: "03",
        name: "abc_oil",
        category: "oil",
        brand: "xcd_brand",
        price: "1000.00",
        url: "./img/oil.jpeg"
    },
        {
          url: 'https://rukminim1.flixcart.com/image/300/300/l51d30w0/shoe/z/w/c/10-mrj1914-10-aadi-white-black-red-original-imagft9k9hydnfjp.jpeg?q=70',
          name: 'TRQ White Shoes',
          category: 'Shoes',
          seller: 'AMZ Seller Ghz',
          price: 1999
        },
        {
          url: 'https://5.imimg.com/data5/KC/PC/MY-38629861/dummy-chronograph-watch-500x500.jpg',
          name: 'LOREM Watch Black',
          category: 'Watches',
          seller: 'Watch Ltd Siyana',
          price: 2599
        },
        {
          url: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQq39iB_cO6uhZ59vubrqVuYTJQH-4Qa0hU9g&usqp=CAU',
          name: 'AMZ Laptop 8GB RAM',
          category: 'Laptops',
          seller: 'Delhi Laptops',
          price: 50000
        },
        {
          url: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQfvoDzLqrT7GwU3z7Ccp0Cl9rV0ZnU9DcmEg&usqp=CAU',
          name: 'Security Camera',
          category: 'CCTV',
          seller: 'Camron LTD',
          price: 4000
        },
        {
          url: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSG9e8Axt-h9q8EIybKfjGzbkIWJAr50_BX7Q&usqp=CAU',
          name: 'Watch Pink',
          category: 'Watches',
          seller: 'Watch Ltd',
          price: 2000
        },
        {
          url: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT9xzgtOpMxdpfgBOg3OKsEqYRkNBbuprJj4w&usqp=CAU',
          name: 'Cup red Color',
          category: 'Cup',
          seller: 'ABS Ltd',
          price: 100
        },
      ])
    const[cart, setCart] = React.useState([]);
    const[showCart, setShowCart] = React.useState(false);
    console.log(cart);
    function addToCart(data){
        setCart([ ...cart, {...data, quantity: 1}]);
    }

    const handleShow = (value)=>{
        setShowCart(value)
    }

    return(
        <div>
            <Header2 handleShow = {handleShow} count = {cart.length}/>;
            {
                showCart? <CartList cart = {cart}></CartList>
                :<Product_list2 product = {product} addToCart = {addToCart}></Product_list2>
            }
            
        </div>
    )
}

export default Shopping_cart;
