import React, { useEffect, useState } from "react";
import axios from "axios";
import Header2 from "./header2";
import Product_list2 from "./product_list2";

function Shopping_cart(props) {
  const [product, setProduct] = useState([]);
  const [cart, setCart] = useState([]);

  const address = props.send;

  const Store = [
    {
      Store_name: "abc_store",
      division: "Chittagong",
      district: "Chittagong",
      thana: "Chittagong",
      url: "./img/store-1.jpg",
    },
    {
      Store_name: "bcd_store",
      division: "Chittagong",
      district: "Chittagong",
      thana: "Chittagong",
      url: "./img/store-1.jpg",
    },
    {
      Store_name: "efg_store",
      division: "Chittagong",
      district: "Chittagong",
      thana: "Chittagong",
      url: "./img/store-1.jpg",
    },
  ];

  function updateProduct() {
    const newProduct = product.filter(
      (item) =>
        item.division === address.divName && item.district === address.disName
    );
    setProduct(newProduct);
  }

  useEffect(() => {
    // Fetch product data from the backend
    axios
      .get("http://localhost:5000/store")
      .then((response) => {
        setProduct(response.data);
      })
      .catch((error) => {
        console.error("Error fetching store data:", error);
      });
  }, [address.divName, address.disName]); // Dependency array to trigger the effect when address.divName or address.disName changes

  useEffect(() => {
    // Call the updateProduct function when the product state is updated
    updateProduct();
  }, [product]); // Dependency array to trigger the effect when product state changes

  function addToCart(data) {
    setCart([...cart, { ...data, quantity: 1 }]);
  }

  return (
    <div>
      <Product_list2 product={product} addToCart={addToCart} />
    </div>
  );
}

export default Shopping_cart;
