import React, { useEffect, useState } from "react";
import axios from "axios";
import Header2 from "./header2";
import Product_list2 from "./product_list2";

function Shopping_cart(props) {
  const [product, setProduct] = useState([]);
  const [cart, setCart] = useState([]);

  const address = props.send;
  const newProduct = [];

  console.log(address);

  const Store = [{
    Store_name: "abc_store",
    division: "Chittagong",
    district: "Chittagong",
    thana: "Chittagong",
    url: "./img/store-1.jpg"
  },
  {
    Store_name: "bcd_store",
    division: "Chittagong",
    district: "Chittagong",
    thana: "Chittagong",
    url: "./img/store-1.jpg"
  },
  {
    Store_name: "efg_store",
    division: "Chittagong",
    district: "Chittagong",
    thana: "Chittagong",
    url: "./img/store-1.jpg"
  }
  ]
  // divName: divName,
  // disName: disName,
  // thanaName: thanaName
  //&& Store.thana == address.thanaName
  function updateProduct(){
    Store.forEach(item =>{
      if(Store.division == address.divName && Store.district == address.disName){
        console.log(item);
        newProduct.push(item);
      }
    })
  }

  useEffect(()=>{
    updateProduct();
    setProduct(newProduct);
    console.log("hello");
  }, [address.divName, address.disName]);

  // useEffect(() => {
  //   // Fetch product data from the backend
  //   axios.get("http://localhost:5000/product")
  //     .then((response) => {
  //       setProduct(response.data);
  //     })
  //     .catch((error) => {
  //       console.error("Error fetching product data:", error);
  //     });
  // }, []); // Empty dependency array to fetch data only once on component mount

  function addToCart(data) {
    setCart([...cart, { ...data, quantity: 1 }]);
  }

  return (
    <div>
      <Product_list2
        product={product}
        addToCart={addToCart}
      ></Product_list2>
    </div>
  );
}

export default Shopping_cart;
