import React from "react";
import Navbar from "./navbar";
import Store_list from "./store_list";
import Img_box1 from "./img-box1";

function Product(){
    const[isClicked, setClick] = React.useState(false);
    function handleClick(){
        setClick(!isClicked);
    }
    return(
        <div className="products">
            {/* <Navbar /> */}
            <div className="upper">
                {!isClicked && (<div>
                    <img onClick={handleClick} className="icon" src="./icon/filter.png"></img>
                </div>) }
                {isClicked && (<div className="sidebar">
                    <img onClick={handleClick} className="icon" src="./icon/filter.png"></img>
                    <h1>Hello Clicked</h1>
                </div>)}
            </div>
            <div className="lower">
                {Store_list.map(store =>{
                    const allitems = store.items;
                    return allitems.map(item =>{
                        return <Img_box1 name = {item.name} url = {item.url} price = {item.price}/>
                    })
                })}
            </div> 

            <div className="upper">

            </div>
        </div>
    )
}

export default Product;
