import React from "react";
import Header2 from "./header2";
import Product_list2 from "./product_list2";
import CartList from "./cartList";


function Shopping_cart(){
    const [product, setProduct] = React.useState([
      {
        id: "01",
        name: "abc_plug",
        category: "plug",
        brand: "xyz_brand",
        price: "800.00",
        url: "./img/plug.png"
    },
    {
        id: "02",
        name: "abc_bulb",
        category: "bulb",
        brand: "efg_brand",
        price: "900.00",
        url: "./img/bulb.jpg"
    },
    {
        id: "03",
        name: "abc_oil",
        category: "oil",
        brand: "xcd_brand",
        price: "1000.00",
        url: "./img/oil.jpeg"
    },
    {
      id: "01",
      name: "abc_plug",
      type: "plug",
      brand: "xyz_brand",
      price: "800.00",
      url: "./img/plug.png"
  },
  {
      id: "02",
      name: "abc_bulb",
      type: "bulb",
      brand: "efg_brand",
      price: "900.00",
      url: "./img/bulb.jpg"
  },
  {
      id: "03",
      name: "abc_oil",
      type: "oil",
      brand: "xcd_brand",
      price: "1000.00",
      url: "./img/oil.jpeg"
  }
      ])
    const[cart, setCart] = React.useState([]);
    const[showCart, setShowCart] = React.useState(false);
    console.log(cart);
    function addToCart(data){
        setCart([ ...cart, {...data, quantity: 1}]);
    }

    const handleShow = (value)=>{
        setShowCart(value)
    }

    return(
        <div>
            <Header2 handleShow = {handleShow} count = {cart.length}/>;
            {
                showCart? <CartList cart = {cart}></CartList>
                :<Product_list2 product = {product} addToCart = {addToCart}></Product_list2>
            }
            
        </div>
    )
}

export default Shopping_cart;
