import React from "react";
import Navbar from "./navbar";
import Store_list from "./store_list";
import Img_box1 from "./img-box1";

function Product() {
  const [isClicked, setClick] = React.useState(false);
  const [isClicked2, setClick2] = React.useState(false);
  const [cartList, setCartList] = React.useState([]);

  function handleClick() {
    setClick(!isClicked);
  }

  function handleClick2() {
    setClick2(!isClicked2);
  }

  const addToCart = (storeName, pId) => {
    setCartList([...cartList, { storeName, id: pId }]);
  };

  return (
    <div className="products">
      {/* Rest of the code */}
      <div className="lower">
        {Store_list.map((store) => {
          const storeName = store.store_name;
          const allitems = store.items;
          return allitems.map((item) => {
            const id = item.id;
            const idName = {
              storeName: storeName,
              pId: id,
            };
            return (
              <Img_box1
                name={item.name}
                url={item.url}
                price={item.price}
                id={idName}
                addToCart={addToCart} // Pass the addToCart function as a prop
              />
            );
          });
        })}
      </div>
    </div>
  );
}

export default Product;
